command:example.dev:code:
# create a byte array for the M-F 8:00 am to 5 pm logon hours $hours = New-Object byte[] 21 $hours[5] = 255; $hours[8] = 255; $hours[11] = 255; $hours[14] = 255; $hours[17] = 255; $hours[6] = 1; $hours[9] = 1; $hours[12] = 1; $hours[15] = 1; $hours[18] = 1;
# create a hashtable to update the logon hours and a description $replaceHashTable = New-Object HashTable $replaceHashTable.Add("logonHours", $hours) $replaceHashTable.Add("description", "Sarah Davis can only logon from Monday through Friday from 8:00 AM to 5:00 PM")
# set the value of the logonHours and description attributes Set-ADUser "SarahDavis" -Replace $replaceHashTable

